mapper.update:
UPDATE BOARD
   SET BOARD_TITLE = #{boardTitle} , BOARD_CONTENT = #{boardContent}
  WHERE BOARD_NO = #{boardNo}

UPDATE BOARD_IMG
   SET STATUS = 'N'
  WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*)
  FROM "BOARD_LIKE"
 WHERE USER_NO = #{userNo}
   AND BOARD_NO = #{boardNo}

mapper.delete:
DELETE
  FROM "BOARD_LIKE"
 WHERE USER_NO = #{userNo}
   AND BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*)
  FROM BOARD_LIKE
 WHERE BOARD_NO = #{boardNo}
