mapper.select:
SELECT TOUR_NO
FROM TOUR
WHERE CONTENT_ID = #{id}

mapper.insert:
INSERT INTO PLANNER(PLAN_NO
				  , PLAN_NAME
				  , PLAN_EXP
				  , START_DATE
				  , END_DATE
				  , AREA_CODE
				  , SIGUNGUCODENO
				  , CHANGE_NAME
				  , REF_UNO)
		    VALUES(PLANNER_NO.NEXTVAL
		         , #{planName}
		         , #{planExp}
		         , #{startDate}
		         , #{endDate}
		         , #{areaCode}	
		         , #{sigunguCodeNo}
		         , #{changeName}
		         , #{refUno})

INSERT INTO PLAN_DETAIL(DETAIL_NO
					  , START_TIME
					  , END_TIME
					  , PLAN_DATE
					  , TIME_AMOUNT
					  , DAY_COUNT
					  , REF_TNO
					  , REF_PNO)
			     VALUES(PLAN_DETAIL_NO.NEXTVAL
			     	  , #{startTime}
			     	  , #{endTime}
			     	  , #{planDate}
			     	  , #{time}
			     	  , #{dayCount}
			     	  , #{refTno}
			     	  , PLANNER_NO.CURRVAL)

mapper.select:
SELECT COUNT(*)
FROM TOUR
WHERE CONTENT_ID = #{contentId}

SELECT * FROM(SELECT ROWNUM RN, PL.* FROM(SELECT P.PLAN_NO,P.PLAN_NAME,P.PLAN_EXP,P.CHANGE_NAME,P.COUNT,LOCATION_NAME,SIGUNGU_NAME FROM PLANNER P JOIN DISTRICTS ON(AREA_CODE=LCODE_NO) LEFT JOIN CITY ON(SIGUNGUCODENO = SIGUNGU_CODE_NO) WHERE P.PL_STATUS = 'Y' AND P.STATUS = 'Y'
ORDER BY P.CREATE_DATE DESC
ORDER BY P.COUNT DESC
) PL) WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM (SELECT ROWNUM RN, PL.* FROM (SELECT P.PLAN_NO,P.PLAN_NAME,P.PLAN_EXP,P.CHANGE_NAME,P.COUNT,LOCATION_NAME,SIGUNGU_NAME FROM PLANNER P JOIN DISTRICTS ON(AREA_CODE=LCODE_NO) LEFT JOIN CITY ON(SIGUNGUCODENO = SIGUNGU_CODE_NO) WHERE P.PL_STATUS = 'Y' AND P.STATUS = 'Y' AND (LOCATION_NAME LIKE '%'||#{keyword}||'%' OR SIGUNGU_NAME LIKE '%'||#{keyword}||'%')
ORDER BY P.CREATE_DATE DESC
ORDER BY P.COUNT DESC
) PL) WHERE RN BETWEEN #{start} AND #{end}

SELECT TOUR_TYPE
FROM TOUR
WHERE CONTENT_ID = #{contentId}

SELECT COUNT(*)
FROM PLAN_REPLY
WHERE REF_PNO = ${pno}
